import React from "react";
import {
  Card,
  CardContent,
  Collapse,
  Typography,
  IconButton,
  CardActionArea,
  CardActions,
} from "@mui/material";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import ExpandLessIcon from "@mui/icons-material/ExpandLess";
import { makeStyles } from "@mui/styles";

const useStyles = makeStyles({
  root: {
    maxWidth: 500,
    width: 500,
  },
  title: {
    color: "#24305E",
  },
  subtitle: {
    color: "#24305E",
  },
  link: {
    color: "#374785",
    "&hover": {
      color: "#A8D0E6",
    },
  },
  text: {
    margin: "8px 0 !important",
  },
  actions: {
    justifyContent: "center",
  },
});

function ItemCard({ id, title, subtitle, link, textList }) {
  const classes = useStyles();
  const [expanded, setExpanded] = React.useState(false);

  const handleExpand = () => {
    setExpanded(!expanded);
  };

  return (
    <Card className={classes.root} key={id} elevation={0}>
      <CardActionArea>
        <CardContent>
          <Typography
            gutterBottom
            variant="h4"
            component="div"
            className={classes.title}
          >
            {title}
          </Typography>
          {subtitle && (
            <Typography
              gutterBottom
              variant="subtitle1"
              component="div"
              className={classes.subtitle}
            >
              {subtitle}
            </Typography>
          )}
          {link && (
            <Typography variant="body2">
              <a href={link} target="_blank" className={classes.link}>
                {link}
              </a>
            </Typography>
          )}
          <Collapse in={expanded} key={`${id}-expanded`}>
            {textList.map((text) => (
              <Typography
                variant="body2"
                color="text.secondary"
                className={classes.text}
              >
                {text}
              </Typography>
            ))}
          </Collapse>
        </CardContent>
      </CardActionArea>
      <CardActions className={classes.actions}>
        <IconButton onClick={handleExpand}>
          {expanded ? <ExpandLessIcon /> : <ExpandMoreIcon />}
        </IconButton>
      </CardActions>
    </Card>
  );
}

export default ItemCard;
